$(document).ready(function(){$(function(){var a=$("#fileupload");a.fileupload({url:a.attr("data-url"),dataType:"json",add:function(h,g){var d=$("<li></li>",{"class":"load-box loader"});$("#files").append(d);var b=new FileReader();var c=$("<img>",{src:URL.createObjectURL(g.files[0]),style:"opacity:0.2;width:120px;height:120px;position:absolute"});d.html(c);var f=g.submit().complete(function(e,k,j){d.append(e.responseText);var i=d.find(".thumbnail-close").find("img");i.on("load",function(){c.remove();d.removeClass("loader")})})}});$("body").on("click",".delete_link",function(){var b=this;var c=$(b).attr("data-url");$(b).parents("li").fadeOut(300);$.post(c,null,function(d){if(d.status==="success"){setTimeout(function(){$(b).parents("li").remove()},300)}},"json");return false});$("body").on("click",".avatar",function(d){d.stopPropagation();if($(this).hasClass("active")){return true}var b=this;var c=$(b).attr("data-url");$(".avatar.active").removeClass("active");$.post(c,null,function(e){if(e){$(b).addClass("active")}},"json")});$(".fancybox").fancybox({afterLoad:function(){var c=this.element.attr("data-id");var b=$("#"+c+"-etalon").clone().attr("id","").show();this.inner.parent("div").append(b)}})})});